"use strict";

var _interopRequireDefault = require("@babel/runtime/helpers/interopRequireDefault");

var _getSortInfo = _interopRequireDefault(require("./getSortInfo"));

describe('getSortInfo', function () {
  it('should return controlled when sortInfo is on props', function () {
    var props = {
      sortInfo: {
        dir: 1,
        name: 'name'
      }
    };
    var state = {};
    expect((0, _getSortInfo.default)(props, state)).toEqual({
      dir: 1,
      name: 'name'
    });
  });
  it('should return uncontrolled when sortInfo is on state', function () {
    var props = {};
    var state = {
      sortInfo: {
        dir: -1,
        name: 'state'
      }
    };
    expect((0, _getSortInfo.default)(props, state)).toEqual({
      dir: -1,
      name: 'state'
    });
  });
});