"use strict";

var _interopRequireDefault = require("@babel/runtime/helpers/interopRequireDefault");

var _clamp = _interopRequireDefault(require("./clamp"));

describe('clamp', function () {
  it('should work when in limits', function () {
    expect((0, _clamp.default)(5, 1, 7)).toEqual(5);
  });
  it('should work when < min', function () {
    expect((0, _clamp.default)(-1, 1, 7)).toEqual(1);
  });
  it('should work when > max', function () {
    expect((0, _clamp.default)(100, 1, 7)).toEqual(7);
  });
  it('should work when no max', function () {
    expect((0, _clamp.default)(100, 1, undefined)).toEqual(100);
    expect((0, _clamp.default)(0, 1, undefined)).toEqual(1);
  });
});